import './styles.css';
import { useState, useEffect } from 'react';

import BoxOutput from './boxoutput.js'


function App() {
  const [turnCount, setTurnCount] = useState(0);
  //const [difficulty, setDifficulty] = useState('');
  const [gameStatus, setGameStatus] = useState('');
  const [code, setCode] =useState([]);

  const codeRef = ['YLW', 'BLU', 'BRN', 'GRN', 'RED', 'VLT']


  const generateCode= () => {

    let res =[]

    for(let i = 0; i < 4 ; i++) {
      res.push(codeRef[Math.floor(Math.random() * codeRef.length)])
    }
    console.log(res)
    setCode(res);

  }



  function codeTest(arr) {
    console.log("Input Array:", arr);
    console.log("Secret Code:", code);

    let ref;

    // Mark correct positions with 'X'
    arr = arr.map((el, i) => (el === code[i] ? 'X' : el));
    console.log("After checking correct positions:", arr);

    if (arr.join('') === 'XXXX') {
      console.log("CODEBREAK!");
      return 'CODEBREAK!';
    }

    if (turnCount === 10) {
      console.log("LOSER");
      return 'LOSER';
    }

    setTurnCount((prevCount) => prevCount + 1);

    // Mark elements to avoid duplicate checking
    ref = code.map((el, i) => (arr[i] === 'X' ? 'X' : el));
    console.log("Reference Array:", ref);

    arr = arr.map((el, i) => {
      if (el === 'X') return el;
      if (ref.includes(el)) {
        ref[ref.indexOf(el)] = null; // Avoid marking the same element twice
        return 'O';
      }
      return 'F';
    });

    console.log("Final Output:", arr.sort().reverse().join(''));
    return arr.sort().reverse().join('');
  }
  useEffect(() => {
    generateCode();
    setGameStatus('starting');
     window.codeTest = codeTest;
    console.log(gameStatus);
  }, [])

  return (
    <div className="App">
      <div className="header">

        <p className="mastermind-logo">MASTERMIND</p>

      </div>

      <div className="backdrop">
        <div className='game-input'>
          <div className="box-input">
            <span className='circle-input'></span>
            <span className='circle-input'></span>
            <span className='circle-input'></span>
            <span className='circle-input'></span>
          </div>

        </div>
        <div className='game-output'>
          <BoxOutput />
        </div>
      </div>


      <div className="footer"></div>
    </div>
  );
}

export default App;
