import './styles.css';
import { useState, useEffect } from 'react';

import BoxOutput from './boxoutput.js'
import BoxInput from './boxinput.js'


function App() {
  const [turnCount, setTurnCount] = useState(0);
  //const [difficulty, setDifficulty] = useState('');
  const [gameStatus, setGameStatus] = useState('');
  const [code, setCode] =useState([]);

  const codeRef = ['YLW', 'BLU', 'BRN', 'GRN', 'RED', 'VLT']


  const generateCode= () => {

    let res =[]

    for(let i = 0; i < 4 ; i++) {
      res.push(codeRef[Math.floor(Math.random() * codeRef.length)])
    }
    console.log(res)
    setCode(res);

  }



  function codeTest(arr) {

    let ref

    arr = arr.map((el, i) => {if (el === code[i]) {return 'X' } else return el}) ;


    // make these two conditions generate a modal 
    if (arr.join('') === 'XXXX') return 'CODEBREAK!';

    if (turnCount == 10) return 'LOSER';
    //

    setTurnCount(prevCount => prevCount + 1);

    ref = code.map((el, i) => { if (arr[i] === 'X') { return 'X'} else return el});

    arr = arr.map((el, i) => {
        if (el === 'X') return el;
        if (ref.includes(el)) return 'O';
        return 'F';
        });

    return arr.sort().reverse().join('');

  }


  useEffect(() => {
    generateCode();
    setGameStatus('starting');
     window.codeTest = codeTest;
    console.log(gameStatus);
  }, [])

  return (
    <div className="App">
      <div className="header">

        <p className="mastermind-logo">MASTERMIND</p>

      </div>

      <div className="backdrop">
        <div className='game-input'>
          <BoxInput />
        </div>
        <div className='game-output'>
          <BoxOutput />
        </div>
      </div>


      <div className="footer"></div>
    </div>
  );
}

export default App;
