import './styles.css';
import { useState, useEffect } from 'react';

function App() {
  const [turnCount, setTurnCount] = useState(0);
  //const [difficulty, setDifficulty] = useState('');
  const [gameStatus, setGameStatus] = useState('starting');
  const [code, setCode] =useState([]);

  const codeRef = ['YLW', 'BLU', 'BRN', 'GRN', 'RED', 'VLT']

  const generateCode= () => {

    let res =[]

    for(let i = 0; i < codeRef.length ; i++) {
      res.push(codeRef[Math.floor(Math.random() * codeRef.length)])
    }
    setCode(res);
  }



  function codeTest(arr) {

    let ref

    arr = arr.map((el, i) => {if (el === code[i]) {
                              return 'X' } else return el}) ;

    if (arr.join('') === 'XXXX') return 'CODEBREAK!';

    if (turnCount == 10) return 'LOSER';

    setTurnCount(prevCount => prevCount + 1);

    ref = code.map((x, i) => { if (arr[i] === 'X') { return 'X'} else return x});

    arr = arr.map((x, i) => {
        if (x === 'X') return x;
        if (ref.includes(x)) return 'O';
        return 'F';
        });

    return arr.sort().reverse().join('');

  }



  return (
    <div className="App">
      <div className="header">
        <p className="mastermind-logo">MASTERMIND</p>
      </div>
      <div className="Mastermind-game-body"></div>
      <div className="footer"></div>
    </div>
  );
}

export default App;
