import './styles.css';
import { useState, useEffect } from 'react';

import generateCode from './GenerateCode.js';
import codeTest from './CodeTest.js'

import circleWhite from './assets/circle-white.png';
import circleBlack from './assets/circle-black.png';
import circleBlue from './assets/circle-blue.png';
import circleRed from './assets/circle-red.png';
import circleYellow from './assets/circle-yellow.png';
import circleOrange from './assets/circle-orange.png';
import circleGreen from './assets/circle-green.png';
import circleViolet from './assets/circle-violet.png';

import circleGrey from './assets/circle-grey.png';




function App() {

  const[testResult, setTestResult] = useState(Array(12).fill(["GRY", "GRY", "GRY", "GRY", "GRY", "GRY", "GRY", "GRY"]))


  const [circleColor1, setCircleColor1] = useState(circleGrey)
  const [circleValue1, setCircleValue1] = useState('')

  const [circleColor2, setCircleColor2] = useState(circleGrey)
  const [circleValue2, setCircleValue2] = useState('')

  const [circleColor3, setCircleColor3] = useState(circleGrey)
  const [circleValue3, setCircleValue3] = useState('')

  const [circleColor4, setCircleColor4] = useState(circleGrey)
  const [circleValue4, setCircleValue4] = useState('')


  const [turnCount, setTurnCount] = useState(0);
  const [gameStatus, setGameStatus] = useState('');
  const [secretCode, setSecretCode] =useState([]);


  //creates the code to break
  useEffect(() =>{    
    setSecretCode(generateCode());;

  },[]);

  console.log(secretCode)

  //gather data from the object 
  const onDragStart = (e,  circleColor, circleValue) => {

    e.dataTransfer.setData('text/circleColor', circleColor);
    e.dataTransfer.setData('text/circleValue', circleValue);

  };

  //enables dropping for the target object 
  const onDragOver = (e) => {
    e.preventDefault();
  };

  //ensures target object holds data from the starting obj  
  const onDrop = (e, target) => {

    const circleColor = e.dataTransfer.getData('text/circleColor');
    const circleValue = e.dataTransfer.getData('text/circleValue');

    if (target === 'target1') {
      setCircleColor1(circleColor);
      setCircleValue1(circleValue);
    } 

    if (target === 'target2'){
      setCircleColor2(circleColor);
      setCircleValue2(circleValue);
    }

    if (target === 'target3'){
      setCircleColor3(circleColor);
      setCircleValue3(circleValue);
    }

    if (target === 'target4'){
      setCircleColor4(circleColor);
      setCircleValue4(circleValue);
    }
  };


  //runs the entire logic
  const runCodeTest = () => {
                    //codeTest is the logic module that determines if the user got it right
    const result = codeTest([circleValue1, circleValue2, circleValue3, circleValue4 ], secretCode)

    const updateArray = (index, newArray) => {
      setTestResult(prevState => {
        const newState = [...prevState];
        newState[index] = newArray;
        return newState;
      });
    };

    if (result.slice(4).every(el => el === 'H') === true) {
      // win show modal
      setGameStatus('Win')
    }

    updateArray(turnCount, result)
    //console.log(turnCount)
    //console.log(testResult[turnCount])
    setTurnCount(Num => Num + 1)

    if (turnCount > 12) return setGameStatus('Lose')

  }

  //results component
  function GameOutput(data) {
    const [results, setResults] = useState(["GRY", "GRY", "GRY", "GRY", "GRY", "GRY", "GRY", "GRY"])

    const colorMap = {
      'WHT': circleWhite,
      'BLK': circleBlack,
      'BLU': circleBlue,
      'RED': circleRed,
      'YLW': circleYellow,
      'ORG': circleOrange,
      'GRN': circleGreen,
      'VLT': circleViolet,
      'GRY': circleGrey,
      'H': circleBlack,
      'M': circleWhite,
      'F':  circleGrey
  };

  setResults(data.array.map(x => colorMap[x]));

  return (
        <div className='game-output'>
          <div className="ref-output">
            <span className='circle-input'><img  alt="circle 1 color"   draggable="false"   src={results[0]}    className='circle-controls-1 '/></span>
            <span className='circle-input'><img  alt="circle 2 color"   draggable="false"   src={results[1]}    className='circle-controls-1'/></span>
            <span className='circle-input'><img  alt="circle 3 color"   draggable="false"   src={results[2]}    className='circle-controls-1'/></span>
            <span className='circle-input'><img  alt="circle 4 color"   draggable="false"   src={results[3]}    className='circle-controls-1'/></span>
          </div>
          <div className="box-output">
            <span ><img  alt="result color" draggable="false" src={results[4]} className='circle-output'/></span>
            <span ><img  alt="result color" draggable="false" src={results[5]} className='circle-output'/></span>
            <span ><img  alt="result color" draggable="false" src={results[6]} className='circle-output'/></span>
            <span ><img  alt="result color" draggable="false" src={results[7]} className='circle-output'/></span>
          </div>
        </div>
    );
  }

  function playAgain() {
    setTurnCount(0);
    setGameStatus('');
    setTestResult(Array(12).fill(["GRY", "GRY", "GRY", "GRY", "GRY", "GRY", "GRY", "GRY"]));
    setCircleColor1(circleGrey);
    setCircleColor2(circleGrey);
    setCircleColor3(circleGrey);
    setCircleColor4(circleGrey);
    setCircleValue1('');
    setCircleValue2('');
    setCircleValue3('');
    setCircleValue4('');
    setSecretCode(generateCode());
  }

  const Outcome =({ gameStatus, turns, playAgain }) => {
  if (gameStatus === '') {
    return null;
  }


  if (gameStatus === 'Win') {
    return (
      <div className="outcome-modal">
        <div className="modal-content">
          <p>You won with {turns} turns!</p>
          <button onClick={playAgain }>Play again?</button>
        </div>
      </div>
    );
  }

  if (gameStatus === 'Lose') {
    return (
      <div className="outcome-modal">
        <div className="modal-content">
          <p>You lost? lol dumbass.</p>
          <button onClick={playAgain }>Play again?</button>
        </div>
      </div>
    );
  }
}


  return (
    <div className="App">
      <div className="header">
        <p className="mastermind-logo">MASTERMIND</p>
      </div>

      {/*game outcome Modal*/}
      <Outcome gameStatus={gameStatus} turns={turnCount} playAgain={playAgain}/>

      <div className="backdrop">
        {/*output circles that will give clues to the player */}
        <span className='top'></span>
        <GameOutput array={testResult[11]} />
        <GameOutput array={testResult[10]} />
        <GameOutput array={testResult[9]} />
        <GameOutput array={testResult[8]} />
        <GameOutput array={testResult[7]} />
        <GameOutput array={testResult[6]} />
        <GameOutput array={testResult[5]} />
        <GameOutput array={testResult[4]} />
        <GameOutput array={testResult[3]} />
        <GameOutput array={testResult[2]} />
        <GameOutput array={testResult[1]} />
        <GameOutput array={testResult[0]} />
        <span className='bot'></span>



        <div className="game-controls">
          <div className='controls1'>
                                            {/*target circles that will hold the choices of user*/}
            <span className='circle-input'><img alt="Control circle Input" draggable="false" src={circleColor1} onDragOver={onDragOver} onDrop={(e) => onDrop(e, 'target1')} className='circle-controls-2'/></span>
            <span className='circle-input'><img alt="Control circle Input" draggable="false" src={circleColor2} onDragOver={onDragOver} onDrop={(e) => onDrop(e, 'target2')} className='circle-controls-2'/></span>
            <span className='circle-input'><img alt="Control circle Input" draggable="false" src={circleColor3} onDragOver={onDragOver} onDrop={(e) => onDrop(e, 'target3')} className='circle-controls-2'/></span>
            <span className='circle-input'><img alt="Control circle Input" draggable="false" src={circleColor4} onDragOver={onDragOver} onDrop={(e) => onDrop(e, 'target4')} className='circle-controls-2'/></span>

                                            {/*checks to see if all inputs are filled*/}
            <button className='buttoncheck' disabled={[circleValue1, circleValue2, circleValue3, circleValue4 ].some(circle => circle === '')} onClick={runCodeTest}> CHECK </button>
          </div>


          <div className='controls2'>
                                            {/*circles choices */}
            <span className='circle-input'><img  alt="Control choices circle Blue"    draggable="true" src={circleBlue}    onDragStart={(e) => onDragStart(e, circleBlue, 'BLU')}     className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  alt="Control choices circle Red"     draggable="true" src={circleRed}     onDragStart={(e) => onDragStart(e, circleRed, 'RED')}      className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  alt="Control choices circle Yellow"  draggable="true" src={circleYellow}  onDragStart={(e) => onDragStart(e, circleYellow, 'YLW')}   className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  alt="Control choices circle Orange"  draggable="true" src={circleOrange}  onDragStart={(e) => onDragStart(e, circleOrange, 'ORG')}   className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  alt="Control choices circle Green"   draggable="true" src={circleGreen}   onDragStart={(e) => onDragStart(e, circleGreen, 'GRN')}    className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  alt="Control choices circle Violet"  draggable="true" src={circleViolet}  onDragStart={(e) => onDragStart(e, circleViolet, 'VLT')}   className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  alt="Control choices circle White"   draggable="true" src={circleWhite}   onDragStart={(e) => onDragStart(e, circleWhite,'WHT')}     className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  alt="Control choices circle Black"   draggable="true" src={circleBlack}   onDragStart={(e) => onDragStart(e, circleBlack, 'BLK')}    className='circle-controls-1 cursor-grab'/></span>         
          </div>

        </div>
      </div>
    </div>
  );
}




export default App;
