    if (turnCount == 10) return 'LOSER';
    //

    setTurnCount(prevCount => prevCount + 1);

    ref = code.map((el, i) => { if (arr[i] === 'X') { return 'X'} else return el});

    arr = arr.map((el, i) => {
        if (el === 'X') return el;
        if (ref.includes(el)) return 'O';
