import './styles.css';
import { useState, useEffect } from 'react';

import GameOutput from './boxoutput.js'
import generateCode, { codeTest } from './GameLogic.js'



function App() {


  const [circleColor1, setCircleColor1] = useState('circle-grey')
  const [circleValue1, setCirclevalue1] = useState('')

  const [circleColor2, setCircleColor2] = useState('circle-grey')
  const [circleValue2, setCirclevalue2] = useState('')

  const [circleColor3, setCircleColor3] = useState('circle-grey')
  const [circleValue3, setCirclevalue3] = useState('')

  const [circleColor4, setCircleColor4] = useState('circle-grey')
  const [circleValue4, setCirclevalue4] = useState('')




  const [turnCount, setTurnCount] = useState(0);
  //const [difficulty, setDifficulty] = useState('');
  const [gameStatus, setGameStatus] = useState('');
  const [code, setCode] =useState([]);

  const secretCode = generateCode()

  useEffect(() =>{    
    setCode(secretCode); 
  },[]);


  return (
    <div className="App">
      <div className="header">

        <p className="mastermind-logo">MASTERMIND</p>

      </div>

      <div className="backdrop">
        <span className='top'></span>
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <span className='bot'></span>

        <div className="game-controls">
          <div className='controls1'>
            <span className={`circle-input ${circleColor1}`}></span>
            <span className={`circle-input ${circleColor2}`}></span>
            <span className={`circle-input ${circleColor3}`}></span>
            <span className={`circle-input ${circleColor4}`}></span>
            <button className='buttoncheck'> CHECK </button>
          </div>
          <div className='controls2'>
            <span  draggable="true" className='circle-input circle-red'></span>
            <span  draggable="true" className='circle-input circle-blue'></span>
            <span  draggable="true" className='circle-input circle-yellow'></span>
            <span  draggable="true" className='circle-input circle-brown'></span>
            <span  draggable="true" className='circle-input circle-orange'></span>
            <span  draggable="true" className='circle-input circle-green '></span>
            <span  draggable="true" className='circle-input circle-white'></span>
            <span  draggable="true" className='circle-input circle-black'></span>          
          </div>

        </div>
      </div>

      <div className="footer"></div>
    </div>
  );
}

export default App;
