import './styles.css';
import { useState, useEffect } from 'react';

import GameOutput from './boxoutput.js'
import generateCode, { codeTest } from './GameLogic.js'

import circleWhite from './assets/circle-white.png';
import circleBlack from './assets/circle-black.png';
import circleBlue from './assets/circle-blue.png';
import circleRed from './assets/circle-red.png';
import circleYellow from './assets/circle-yellow.png';
import circleOrange from './assets/circle-orange.png';
import circleGreen from './assets/circle-green.png';
import circleViolet from './assets/circle-violet.png';

import circleGrey from './assets/circle-grey.png';




function App() {


  const [circleColor1, setCircleColor1] = useState(circleGrey)
  const [circleValue1, setCircleValue1] = useState('')

  const [circleColor2, setCircleColor2] = useState(circleGrey)
  const [circleValue2, setCircleValue2] = useState('')

  const [circleColor3, setCircleColor3] = useState(circleGrey)
  const [circleValue3, setCircleValue3] = useState('')

  const [circleColor4, setCircleColor4] = useState(circleGrey)
  const [circleValue4, setCircleValue4] = useState('')

  //setTurnCount(prevCount => prevCount + 1);


  const [turnCount, setTurnCount] = useState(0);
  //const [difficulty, setDifficulty] = useState('');
  const [gameStatus, setGameStatus] = useState('');
  const [secretCode, setSecretCode] =useState([]);



  useEffect(() =>{    
    setSecretCode(generateCode());;
  },[]);

  const onDragStart = (e, setCircleColor, circleColor, setSrcState, srcState) => {

    e.dataTransfer.setData('text/circleColor', circleColor);
    e.dataTransfer.setData('text/circleValue', circleValue);
  };

  const onDragOver = (e) => {
    e.preventDefault();
  };

  const onDrop = (e, target) => {
    const circleColor = e.dataTransfer.getData('text/circleColor');
    const circleValue = e.dataTransfer.getData('text/circleValue');

    if (target === 'target1') {
      setCircleColor1(circleColor);
      setCircleValue1(circleValue);
    } 

    if (target === 'target2'){
      setCircleColor2(circleColor);
      setCircleValue2(circleValue);
    }

    if (target === 'target3'){
      setCircleColor3(circleColor);
      setCircleValue3(circleValue);
    }

    if (target === 'target4'){
      setCircleColor4(circleColor);
      setCircleValue4(circleValue);
    }

    console.log(circleColor);
    console.log(circleValue);
  };

  return (
    <div className="App">
      <div className="header">
        <p className="mastermind-logo">MASTERMIND</p>
      </div>

      <div className="backdrop">
        <span className='top'></span>
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <GameOutput />
        <span className='bot'></span>

        <div className="game-controls">
          <div className='controls1'>
            <span className='circle-input'><img  draggable="false" src={circleColor1} onDragOver={onDragOver} onDrop={(e) => onDrop(e, 'target1')} className='circle-controls-2'/></span>
            <span className='circle-input'><img  draggable="false" src={circleColor2} onDragOver={onDragOver} onDrop={(e) => onDrop(e, 'target2')} className='circle-controls-2'/></span>
            <span className='circle-input'><img  draggable="false" src={circleColor3} onDragOver={onDragOver} onDrop={(e) => onDrop(e, 'target3')} className='circle-controls-2'/></span>
            <span className='circle-input'><img  draggable="false" src={circleColor4} onDragOver={onDragOver} onDrop={(e) => onDrop(e, 'target4')} className='circle-controls-2'/></span>
            <button className='buttoncheck'> CHECK </button>
          </div>
          <div className='controls2'>
            <span className='circle-input'><img  draggable="true" src={circleBlue}  onDragStart={(e) => onDragStart(e, circleBlue)}  className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  draggable="true" src={circleRed}     className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  draggable="true" src={circleYellow}  className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  draggable="true" src={circleOrange}  className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  draggable="true" src={circleGreen}   className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  draggable="true" src={circleViolet}  className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  draggable="true" src={circleWhite}   className='circle-controls-1 cursor-grab'/></span>
            <span className='circle-input'><img  draggable="true" src={circleBlack}   className='circle-controls-1 cursor-grab'/></span>         
          </div>

        </div>
      </div>

      <div className="footer"></div>
    </div>
  );
}

export default App;
